create table if not exists users (customer_id bigint not null auto_increment, account_type integer not null, address_line1 varchar(255), address_line2 varchar(255), address_line3 varchar(255), cell integer not null, city varchar(255), email varchar(255), first_name varchar(50) not null, is_approved bit not null, last_name varchar(50) not null, phone integer not null, state varchar(255), zip integer not null, primary key (customer_id)) engine=MyISAM
